import React from "react";
import Login from "./pages/Login";

// Giriş yapıldı mı kontrol
const isAuthenticated = () => {
  const token = localStorage.getItem("token");
  return !!token;
};

function App() {
  return (
    <div>
      {!isAuthenticated() ? (
        <Login />
      ) : (
        <div>
          <h2 style={{textAlign: "center", marginTop: 60}}>Hoşgeldiniz!</h2>
          {/* Buraya yönlendirme, navbar veya ana panel component'leri eklenecek */}
        </div>
      )}
    </div>
  );
}

export default App;
