import React, { useEffect, useState } from "react";
import StatusUpdate from "./StatusUpdate";
import ExcelDetailsPanel from "./ExcelDetailsPanel";
import "../styles/FileList.css";

const STATUS_OPTIONS = [
  { value: "", label: "Tümü" },
  { value: "bekliyor", label: "Bekliyor" },
  { value: "ilk_arama", label: "İşlemde" },
  { value: "islem_tamamlandi", label: "İşlem Tamamlandı" },
  { value: "yakin_takip", label: "Yakın Takip" },
  { value: "takip", label: "Takip" },
  { value: "uzak_takip", label: "Uzak Takip" },
  { value: "numara_hatali", label: "Numara Hatalı" },
  { value: "ilgisiz", label: "İlgisiz" },
  { value: "gercek_hesap", label: "Gerçek Hesap" },
  { value: "erisim_yok", label: "Erişim Yok" },
  { value: "mesgul", label: "Meşgul" },
];

function FileList() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filterStatus, setFilterStatus] = useState("");

  const token = localStorage.getItem("token");
  const userRole = localStorage.getItem("userRole");

  useEffect(() => {
    fetchFiles();
    // eslint-disable-next-line
  }, [filterStatus]); // filterStatus değişince yeniden çek

  const fetchFiles = async () => {
    setLoading(true);
    try {
      // Statü filtre parametresi ekle
      let url = "http://localhost:5000/api/files";
      if (filterStatus) {
        url += `?status=${encodeURIComponent(filterStatus)}`;
      }

      const response = await fetch(url, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setFiles(data.files || []);
    } catch {
      setFiles([]);
    }
    setLoading(false);
  };

  const handleDownload = async (filename) => {
    window.open(`http://localhost:5000/api/files/download/${filename}`, "_blank");
  };

  return (
    <div className="filelist-container">
      <h3>{userRole === "admin" ? "Tüm Yüklemeler" : "Yüklediğin Dosyalar"}</h3>

      {/* Filtre Dropdown */}
      {userRole === "admin" && (
        <div style={{ marginBottom: "12px" }}>
          <label htmlFor="status-filter" style={{ marginRight: 8, fontWeight: 600 }}>
            Statü Filtre:
          </label>
          <select
            id="status-filter"
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            style={{ padding: "6px 10px", borderRadius: 6, border: "1px solid #ccc" }}
          >
            {STATUS_OPTIONS.map((opt) => (
              <option key={opt.value} value={opt.value}>
                {opt.label}
              </option>
            ))}
          </select>
        </div>
      )}

      {loading ? (
        <div className="filelist-loading">Yükleniyor...</div>
      ) : files.length === 0 ? (
        <div className="filelist-empty">Henüz dosya yok.</div>
      ) : (
        <>
          <table className="filelist-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Dosya Adı</th>
                <th>Yükleme Tarihi</th>
                <th>Statü</th>
                <th>İndir</th>
              </tr>
            </thead>
            <tbody>
              {files.map((file, i) => (
                <tr key={file.id}>
                  <td>{i + 1}</td>
                  <td>{file.filename}</td>
                  <td>{new Date(file.upload_date).toLocaleString()}</td>
                  <td>
                    {userRole === "admin" ? (
                      <StatusUpdate
                        fileId={file.id}
                        currentStatus={file.status}
                        onUpdated={fetchFiles}
                      />
                    ) : (
                      <span className={`filelist-status filelist-status-${file.status}`}>
                        {file.status}
                      </span>
                    )}
                  </td>
                  <td>
                    <button
                      className="filelist-download-btn"
                      onClick={() => handleDownload(file.filename)}
                    >
                      İndir
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Detay paneli */}
          {userRole === "admin" && (
            <div style={{ marginTop: "38px" }}>
              <ExcelDetailsPanel files={files} onUpdate={fetchFiles} />
            </div>
          )}
          {userRole !== "admin" && (
            <div style={{ marginTop: "38px" }}>
              <ExcelDetailsPanel files={files} readOnly />
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default FileList;
