import React, { useState } from "react";
import "../styles/ExcelDetailsPanel.css";

const DETAIL_FIELDS = [
  { key: "aranmadi", label: "Aranmadı" },
  { key: "tekrar_aranacak", label: "Tekrar Aranacak" },
  { key: "yakin_takip", label: "Yakın Takip" },
  { key: "takip", label: "Takip" },
  { key: "uzak_takip", label: "Uzak Takip" },
  { key: "numara_hatalı", label: "Numara Hatalı" },
  { key: "ilgisiz", label: "İlgisiz" },
  { key: "gercek_hesap", label: "Gerçek Hesap" },
  { key: "erisim_yok", label: "Erişim Yok" },
  { key: "mesgul", label: "Meşgul" },
  { key: "toplam", label: "Toplam" }
];

function ExcelDetailsPanel({ files, onUpdate, readOnly }) {
  const [detailsState, setDetailsState] = useState({});

  const handleInputChange = (fileId, field, value) => {
    setDetailsState(prev => ({
      ...prev,
      [fileId]: { ...prev[fileId], [field]: value }
    }));
  };

  const handleSave = async (fileId) => {
    const details = detailsState[fileId] || {};
    try {
      const token = localStorage.getItem("token");
      const response = await fetch(
        `http://localhost:5000/api/files/${fileId}/details`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`
          },
          body: JSON.stringify({ details })
        }
      );
      if (response.ok) {
        if (onUpdate) onUpdate();
      } else {
        alert("Kaydetme sırasında hata oluştu!");
      }
    } catch {
      alert("Sunucu hatası!");
    }
  };

  return (
    <div className="details-panel">
      <h4>Excel Detayları</h4>
      <div className="details-table">
        <div className="details-row details-header">
          <div className="details-cell details-filename">Dosya Adı</div>
          {DETAIL_FIELDS.map(field => (
            <div className="details-cell" key={field.key}>{field.label}</div>
          ))}
          { !readOnly && <div className="details-cell details-actions"></div> }
        </div>
        {files.map(file => (
          <div className="details-row" key={file.id}>
            <div className="details-cell details-filename">{file.filename}</div>
            {DETAIL_FIELDS.map(field => {
              const value = (
                detailsState[file.id]?.[field.key] ??
                file.details?.[field.key] ??
                ""
              );
              return (
                <div className="details-cell" key={field.key}>
                  {readOnly ? (
                    <span>{value}</span>
                  ) : (
                    <input
                      type="text"
                      value={value}
                      onChange={e => handleInputChange(file.id, field.key, e.target.value)}
                      className="details-input"
                      placeholder={field.label}
                    />
                  )}
                </div>
              );
            })}
            {!readOnly && (
              <div className="details-cell details-actions">
                <button
                  className="details-save-btn"
                  onClick={() => handleSave(file.id)}
                >
                  Kaydet
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ExcelDetailsPanel;
